# Función para convertir de ADC a milivoltios (mV)
def ADCtomV(ADC, n=10, VCC=3.3):
    volts = (((ADC / (2 ** n)) - (1 / 2)) * VCC) / 1009
    return volts * 1000  # Convertir a milivoltios

# Nombres de columnas comunes
columnas = ["Tiempo", "0.1", "0.2", "0.3", "0.4", "EMG"]

fs = 1000  # Hz

# Función para recortar señal
def procesar_emg_csv(nombre_archivo, tiempo_max):
    df = pd.read_csv(nombre_archivo, header=None, names=columnas)
    df["Tiempo"] = np.arange(len(df)) / fs
    df = df[df["Tiempo"] < tiempo_max].copy()
    df["EMG_mV"] = ADCtomV(df["EMG"])
    return df


df_biceps  = procesar_emg_csv("BICEPS.csv", 180)
df_triceps = procesar_emg_csv("TRICEPS.csv", 9999)
df_triceps = df_triceps[(df_triceps["Tiempo"] >= 180) & (df_triceps["Tiempo"] <= 300)].copy()
df_hombro  = procesar_emg_csv("HOMBRO.csv", 150)


