sig = df_biceps["EMG_mV"].to_numpy()
wavelet = 'sym4'
level = 10
coeffs_biceps = pywt.wavedec(sig, wavelet, level=level)

# 2. Umbralización
threshold = np.median(np.abs(coeffs_biceps[-1])) / 0.6745 * np.sqrt(2 * np.log(len(sig)))
coeffs_thresholded = [coeffs_biceps[0]]
for detail_coeff in coeffs_biceps[1:]:
    coeffs_thresholded.append(pywt.threshold(detail_coeff, threshold, mode='soft'))

# 3. Reconstrucción
sig_filtered = pywt.waverec(coeffs_thresholded, wavelet)

for i, c in enumerate(coeffs_biceps):
    plt.figure()
    plt.plot(c)
    plt.title(f"BÍCEPS - Nivel {i} - {'A' if i==0 else 'D'+str(len(coeffs_biceps)-i)}")
    plt.grid(True)
    plt.tight_layout()
    plt.show()

